import React, { useState, useEffect } from 'react';
import './App.css';

const WORDS = ["volkan", "defne", "necla"];

function App() {
  const [gameState, setGameState] = useState({
    onPlay: false,
    inputValue: "",
    message: "",
    correctWord: "",
    scrambledWord: ""
  });

  const handleButtonClick = () => {
    const { inputValue, correctWord } = gameState;
    if (inputValue !== "") {
      const message = correctWord === inputValue ? "Correct Answer" : "Wrong Answer";
      setGameState({ ...gameState, message });
    }
  };

  const handleInputChange = (event) => {
    setGameState({ ...gameState, inputValue: event.target.value });
  };

  const selectWord = () => {
    fetch(`https://random-word-api.herokuapp.com/word`)
    .then((response)=> response.json())
    .then((data)=>{
      return data
    })

  };

  const handleStartGame = () => {
    const word = selectWord();
    const scrambledWord = createScrambledWord(word);
    setGameState({
      onPlay: true,
      inputValue: "",
      message: "",
      correctWord: word,
      scrambledWord
    });
  };

  const createScrambledWord = (word) => {
    const shuffledArray = word.split('');
    for (let i = shuffledArray.length - 1; i > 0; i--) {
      const j = Math.floor(Math.random() * (i + 1));
      [shuffledArray[i], shuffledArray[j]] = [shuffledArray[j], shuffledArray[i]];
    }
    return shuffledArray.join('');
  };

  useEffect(() => {
    let clearMessage;
    if (gameState.message === "Wrong Answer") {
      clearMessage = setTimeout(() => setGameState({ ...gameState, message: "" }), 800);
    }
    return () => {
      if (clearMessage) {
        clearTimeout(clearMessage);
      }
    };
  }, [gameState.message]);

  const { onPlay, inputValue, message, correctWord, scrambledWord } = gameState;

  return (
    <div className='word-app'>
      {message && 
        <div className='message'>
          <p>{message}</p>
        </div>
      }
      <h1>Welcome to Word App</h1>
      <div className='content'>
        {onPlay ? (
          <>
            <div className='board'>
              {correctWord.split('').map((el, i) => (
                <span key={`${el}_${i}`} className='square-bg'>{inputValue[i]}</span>
              ))}
            </div>
            <p className='scrambled-word'>{scrambledWord}</p>
            <div className='field'>
              <input type="text" onChange={handleInputChange} value={inputValue}/>
              <button type='button' onClick={handleButtonClick}>Enter</button>
            </div>
          </>
        ) : (
          <button className='start-button' onClick={handleStartGame}>Start Game</button>
        )}
        {onPlay && (
          <button className='start-button new' onClick={() => setGameState({ ...gameState, onPlay: false })}>New Game</button>
        )}
      </div>
    </div>
  );
}

export default App;
