import { useState } from 'react';
import './App.css';

const WORDS = ["volkan", "defne", "necla"]

function App() {

  const [onPlay, setOnPlay] = useState(false)
  const [inputValue, setInputValue] = useState("") 
  const [message, setMessage] = useState("")
  const [correctWord, setCorrectWord] = useState("")
  const [scrambledWord, setScrambledWord] = useState("")


  const handleButtonClick = ()=>{
    if(correctWord === inputValue){
      setMessage("Correct Answer")
    }else{
      setMessage("Wrong Answer")
    }
  }

  const handleInputChange = (event)=>{
    setInputValue(event.target.value)
  }

  const selectWord = ()=>{
    const randomIndex = Math.floor(Math.random() * WORDS.length);
    return WORDS[randomIndex]
  }

  const handleStartGame = ()=>{
    setOnPlay(true)
    setInputValue("")

    const word = selectWord();
    setCorrectWord(word); 

    setScrambledWord(createScrambledWord(word))
  }

  const createScrambledWord = (word)=>{
    const shuffledArray = word.split('');
    for(let i = shuffledArray.length; i > 0; i--){
      const j = Math.floor(Math.random * (i + 1));
      [shuffledArray[i], shuffledArray[j]] = [shuffledArray[j], shuffledArray[i]]
    }
    return shuffledArray.join('')
  }

  return (
    <div className='word-app'>
      {message && 
      (<div className='message'>
        <p>Wrong Answer</p>
      </div>
      )}

      <h1>Welcome to Word App</h1>

      <div className='content'>

        {onPlay ? (
          <>
          <div className='board'>
            {correctWord.split('').map((el, i)=>(
              <span key={`${el}_${i}`} className='square-bg'>{inputValue[i]}
              </span>

            ))}

          </div>
          <p className='scrambled-word'>{scrambledWord}</p>
          <div className='field'>
            <input type="text" onChange={handleInputChange} value={inputValue}/>
            <button type='button' onClick={handleButtonClick}>Enter</button>
          </div>
        </>) :
        (<button 
          className='start-button' 
          onClick={handleStartGame}>Start Game</button>
        )}

        {onPlay && (<button className='start-button new'>New Game</button>)}

      </div>
    </div>
  );
}

export default App;
