import { useState } from 'react';
import './App.css';

const WORDS = ["volkan", "defne", "necla"]

function App() {

  const [onPlay, setOnPlay] = useState(false)
  const [inputValue, setInputValue] = useState("") 
  const [message, setMessage] = useState(null)
  const [correctWord, setCorrectWord] = useState("")
  const [userWord, setUserWord] = useState("")
  const handleButtonClick = ()=>{
    console.log("click")
  }

  const handleInputChange = (event)=>{
    setInputValue(event.target.value)
  }

  const selectWord = ()=>{
    const randomIndex = Math.floor(Math.random() * WORDS.length);
  }

  const handleStartGame = ()=>{
    setOnPlay(true)
    setInputValue(true)

    const word = selectWord();
    console.log(word)
  }

  return (
    <div className='word-app'>
      {message && 
      (<div className='message'>
        <p>Wrong Answer</p>
      </div>
      )}

      <h1>Welcome to Word App</h1>

      <div className='content'>

        {onPlay ? (
          <>
          <div className='board'>
          <p className='square-bg'></p>
          </div>
          <p className='scrambled-word'></p>
          <div className='field'>
            <input type="text" onChange={handleInputChange} value={inputValue}/>
            <button type='button' onClick={handleButtonClick}>Enter</button>
          </div>
        </>) :
        (<button 
          className='start-button' 
          onClick={handleStartGame}>Start Game</button>
        )}

        {onPlay && (<button className='newgame-button'>New Game</button>)}

      </div>
    </div>
  );
}

export default App;
